drop database agt;
create database agt;
use agt;

CREATE TABLE municipios (
    id_municipio INT AUTO_INCREMENT PRIMARY KEY,
    nome VARCHAR(255) NOT NULL,
    estado CHAR(2) NOT NULL
);

CREATE TABLE logins (
    id_login INT AUTO_INCREMENT PRIMARY KEY,
    login VARCHAR(100) NOT NULL UNIQUE,
    senha VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL UNIQUE,
    nivel_acesso ENUM('Admin', 'User') NOT NULL
);

CREATE TABLE aliquotas (
    id_aliquota INT AUTO_INCREMENT PRIMARY KEY,
    id_municipio INT NOT NULL,
    cod_servico DECIMAL(10,2) NOT NULL,
    desc_servico TEXT NOT NULL,
    aliquota_iss DECIMAL(5,2) NOT NULL,
    lei_vigente VARCHAR(255),
    FOREIGN KEY (id_municipio) REFERENCES municipios(id_municipio)
);

CREATE TABLE unidades (
    id_unidade INT AUTO_INCREMENT PRIMARY KEY,
    cod_entidade VARCHAR(50) NOT NULL,
    cnpj VARCHAR(20) NOT NULL UNIQUE,
    nome_fantasia VARCHAR(255),
    endereco TEXT,
    id_municipio INT NOT NULL,
    cep VARCHAR(15),
    FOREIGN KEY (id_municipio) REFERENCES municipios(id_municipio)
);

CREATE TABLE cadastro (
    id_cadastro INT AUTO_INCREMENT PRIMARY KEY,
    id_unidade INT NOT NULL,
    id_municipio INT NOT NULL,
    cnpj_cadastro VARCHAR(20),
    sistema VARCHAR(255),
    site VARCHAR(2048),
    login_sistema VARCHAR(255),
    senha_sistema VARCHAR(255),
    dia_vencimento INT CHECK (dia_vencimento BETWEEN 1 AND 31),
    observacao VARCHAR(2048),
    FOREIGN KEY (id_unidade) REFERENCES unidades(id_unidade),
    FOREIGN KEY (id_municipio) REFERENCES municipios(id_municipio)
);

CREATE TABLE contador_relatorios (
    id INT PRIMARY KEY AUTO_INCREMENT,
    quantidade INT NOT NULL
);


